  font-size: 16px;
  --color-pink: #e75480;
  --color-grey: rgb(238, 238, 238);
  --color-greyDarker: rgb(84, 84, 84);
}

* {
  box-sizing: border-box;
}

.smallText {
  font-size: 0.7rem;
  font-weight: 300;
}

body {
  margin: 0;
  font-family: sans-serif;
}

.container {
  width: calc(100% - 120px);
  margin: auto;
}

