  font-weight: 600;
  color: var(--color-pink);
}
header .container {
  display: flex;
  align-items: center;
}
header .searchBar {
  margin-left: auto;
}
header .header-option {
  padding-left: 5rem;
  letter-spacing: 0.6px;
  display: flex;
  align-items: center;
}
