  padding: 0.6rem 0;
}
.listings .listings-grid .listings-grid-element .text-lower span {
  color: var(--color-greyDarker);
}
.listings .listings-grid .listings-grid-element .text {
  display: flex;
  align-items: center;
  border-bottom: 1px solid #ddd;
